import React, { useState } from 'react'

class FriendList extends React.Component {
    constructor(props){
        super(props)

        this.state = {
            name : 'Will',
            friends: ['mike', 'jeff']
        }

        this.addFriend = this.addFriend.bind(this)
    }

    addFriend(friend) {
        this.setState((state) => {
            friends: state.friends.concat([friend])
        })
    }

    render() {
        return(
            <div>
                <h3> {this.state.name}'s Friends: </h3>
                <br />
                <Friends list = {this.state.friends}/>
                <NewFriends addFriend = {this.addFriend} />
            </div>
        )
    }

}

function Friends(props) {
    return(
        <div>
            <ul>
                {props.list.map(item => <li>{item}</li>)}
            </ul>
        </div>
    );
}

function NewFriends(props) {
    const [ friend, setFriend ] = useState('')

    return(
        <div>
            New Friends 
            <input type = 'text' value = {friend} onChange = {setFriend (friend = e.target.value)} />
            <button onClick = {props.addFriend}> Add Friend </button>
        </div>
    )
}

export default FriendList;
